update.foreach:
INTO LINE_SEQ(
	LINE_TURN
	, APPR_NO
	, APPR_SIT_CD
	, APPR_DT
	, LINE_YN
	, EMP_CD
	, APPR_NM
	, SIGN
	)
VALUES(
	#{lineSeqVO.lineTurn}
	,#{lineSeqVO.apprNo}
	,#{lineSeqVO.apprSitCd}
    ,#{lineSeqVO.apprDt}
    ,#{lineSeqVO.lineYn}
    ,#{lineSeqVO.empCd}
    ,#{lineSeqVO.apprNm}
    ,#{lineSeqVO.sign}
    )

mapper.select:
SELECT SIGN
FROM   EMPLOYEE
WHERE  EMP_CD = #{empCd}

SELECT SIGN
FROM   LINE_SEQ
WHERE  APPR_NO = #{apprNo}

mapper.update:
UPDATE LINE_SEQ
SET    SIGN = #{sign}
     , APPR_SIT_CD = 'A02'
     , LINE_YN = 'Y'
WHERE  APPR_NO = #{apprNo}
AND    APPR_SIT_CD = 'A01'
