mapper.select:
SELECT COUNT(EMP_ID)
FROM    EMPLOYEE
WHERE   EMP_ID = #{empId}

SELECT COUNT(URL)
FROM    COMPANY
WHERE   URL = #{coCd}

SELECT NVL(EMP_ID, 0)
FROM   EMPLOYEE
WHERE  EMP_NM = #{empNm}
AND    EMP_EML = #{empEml}

mapper.update:
UPDATE  EMPLOYEE
SET     EMP_PW = #{empPw}
WHERE   EMP_ID = #{empId}
AND     EMP_EML = #{empEml}

mapper.select:
SELECT  EMP_NM
FROM    EMPLOYEE
WHERE   EMP_ID = #{empId}
AND     EMP_EML = #{empEml}
