update.foreach:
			INTO TODO(TODO_CD
					  , TODO_NO
			          , TIT
			          , CON
			          , REG_TI
			          , END_TI
			          , CTOR
			          , TYPE
			          , SIT
			          , EMP_CD) VALUES
      				((SELECT TO_CHAR(SYSDATE, 'YYMMDD')||TO_CHAR(NVL(MAX(SUBSTR(TODO_CD,7)),0)+1, 'FM000')
				        FROM TODO
				        WHERE TODO_CD LIKE TO_CHAR(SYSDATE, 'YYMMDD')||'%')+#{index}
			          , #{todo.todoNo}
				      , #{todo.tit}
				      , #{todo.con}
				      , #{todo.regTi}
				      , #{todo.endTi}
				      , #{todo.ctor}
				      , #{todo.type}
				      , 'TODO00'
				      , #{todo.empNm})

mapper.update:
UPDATE  TODO
SET     TIT = #{tit}
      , CON = #{con}
      , REG_TI = #{regTi}
      , END_TI = #{endTi}
      , PROGS = #{progs}
WHERE   TODO_NO = #{todoNo}

UPDATE  TODO
SET     SIT = #{sit}
WHERE   TODO_NO = #{todoNo}
